<div class="byk-field" class:byk-has-label={!!label} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  <div class="byk-field__control">
    <label for={fieldId} class="byk-field__label">
      {#if label}
        <span>{label}</span>
      {/if}
      <input id={fieldId} type="checkbox" class="byk-field__checkbox" bind:checked={value} use:validate={value} {disabled} on:change on:blur />
      <div class="byk-field__indicator" class:byk-field__indicator--invalid={$validity.dirty && !$validity.valid}></div>
    </label>
    <div class="byk-field__description">
      <slot name="description"></slot>
    </div>
    {#if validationHints && $validity.dirty && !$validity.valid}
    <p class="byk-field__help" transition:fade|local>{$validity.message}</p>
    {/if}
  </div>
</div>

<style lang="scss">
  .byk-field {
		display: flex;
    flex-shrink: 0;
		flex-direction: column;
	}

  .byk-field.byk-has-label {
    margin: 0.6rem 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
    color: var(--byk-c-label);
    cursor: pointer;
	}
	
	.byk-field .byk-field__control {
		display: flex;
    flex-direction: column;
	}
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}

  .byk-field__control {
    display: block;
    position: relative;
    padding-left: 30px;
    margin-bottom: 5px;

    input {
      position: absolute;
      z-index: -1;
      opacity: 0;
    }

    .byk-field__indicator {
      position: absolute;
      top: 2px;
      left: 0;
      height: 20px;
      width: 20px;
      border: 1px solid var(--byk-c-grey-light);
      border-radius: 2px;
      background: var(--byk-c-input-background);

      &.byk-field__indicator--invalid {
        border: var(--byk-c-danger);
      }
    }
  }

  .byk-field__control input:focus ~ .byk-field__indicator {
    background: var(--byk-c-primary-lightest);
  }

  .byk-field__control:hover input ~ .byk-field__indicator {
    background: var(--byk-c-white);
  }

  .byk-field__control input:checked ~ .byk-field__indicator {
    background: var(--byk-c-input-background);
  }

  .byk-field__control:hover input:not([disabled]):checked ~ .byk-field__indicator,
  .byk-field__control input:checked:focus ~ .byk-field__indicator {
    background: var(--byk-c-grey-light);
  }

  .byk-field__control input:disabled ~ .byk-field__indicator {
    background: var(--byk-c-input-background);
    opacity: 0.6;
    pointer-events: none;
  }

  .byk-field__indicator:after {
    content: '';
    position: absolute;
    display: none;
  }

  .byk-field__control input:checked ~ .byk-field__indicator:after {
    display: block;
  }

  .byk-field__indicator:after {
    left: 8px;
    top: 4px;
    width: 5px;
    height: 10px;
    border: solid var(--byk-c-primary);
    border-width: 0 2px 2px 0;
    transform: rotate(45deg);
  }

  .byk-field__checkbox input:disabled ~ .byk-field__indicator:after {
    border-color: var(--byk-c-disabled);
  }

  .byk-field__description {
    font-weight: normal;
    display: flex;
  }
</style>

<script>
  import { fade } from 'svelte/transition'

  const fieldId = `byk_id_${Math.random().toString(36).substring(0, 8)}`

  export let value
  export let validate
  export let validity
  export let label = false
  export let validationHints = true
  export let disabled = false
</script>