<div class="field byk-field" class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  <span class="byk-field__label label">{label}</span>
  <div class="byk-field__control checkbox-group">
    <div class="options">
      <div class="byk-checkbox-group__options__items">
        {#each options as option, i (option.value)}
        <label for={'byk-checkbox-group-' + i} class="control control-checkbox">{option.name}
          <input type="checkbox" id={'byk-checkbox-group-' + i} bind:group={group} value={option.value} use:validate={group} />
          <div class="indicator"></div>
        </label>
        {/each}
      </div>
      <div class="byk-checkbox-group__options__validation-messages">
        <span class="byk-field__icon field-icon icon is-medium is-right" class:byk-is-danger={!$validity.valid} class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
        </span>
        {#if validationHints && $validity.dirty && !$validity.valid}
        <p class="byk-field__help" transition:fade>{$validity.message}</p>
        {/if}
      </div>
    </div>
  </div>
</div>

<style type="text/scss">
  .byk-field {
    flex-shrink: 0;
  }

  .byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}

  span {
    font-size: 18px;
    font-weight: 800;
  }

  .byk-field.byk-field--invalid .byk-field__control .control .indicator {
		border: 1px solid var(--byk-c-danger);
  }

  .byk-field .byk-field__control .byk-checkbox-group__options__validation-messages {
    margin: 24px;
    display: flex;
    align-items: center;
  }

  .checkbox-group {
    margin: 12px 0;
    display: flex;

    label, input {
      font-size: 16px;
    }

    .icon {
      display: flex;
      align-items: flex-start;
    }

    .options {
      display: flex;
      flex: 1;
    }
  }

  .control {
    display: block;
    position: relative;
    padding-left: 30px;
    margin-bottom: 5px;
    cursor: pointer;
    font-size: 18px;

    input {
      position: absolute;
      z-index: -1;
      opacity: 0;
    }

    .indicator {
      position: absolute;
      top: 2px;
      left: 0;
      height: 20px;
      width: 20px;
      border: 1px solid var(--byk-c-control-border);
      border-radius: 2px;
      background: var(--byk-c-input-background);
    }
  }

	:global(.byk-field .byk-field__control .byk-field__icon svg) {
		width: 1.25rem;
		height: 1.25rem;
    stroke: var(--byk-c-primary);
	}

  :global(.byk-field .byk-field__control .byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field .byk-field__control .byk-is-success svg) {
		stroke: var(--byk-c-primary);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		color: var(--byk-c-danger);
	}

  .control:hover input ~ .indicator,
  .control input:focus ~ .indicator {
    background: var(--byk-c-input-focus-background);
  }

  .control input:checked ~ .indicator {
    background: var(--byk-c-input-background);
  }

  .control:hover input:not([disabled]):checked ~ .indicator,
  .control input:checked:focus ~ .indicator {
    background: var(--byk-c-input-background);
  }

  .control input:disabled ~ .indicator {
    background: var(--byk-c-input-disabled-background);
    opacity: 0.6;
    pointer-events: none;
  }

  .indicator:after {
    content: '';
    position: absolute;
    display: none;
  }

  .control input:checked ~ .indicator:after {
    display: block;
  }

  .control-checkbox .indicator:after {
    left: 8px;
    top: 4px;
    width: 5px;
    height: 10px;
    border: solid var(--byk-c-primary);
    border-width: 0 2px 2px 0;
    transform: rotate(45deg);
  }

  .control-checkbox input:disabled ~ .indicator:after {
    border-color: var(--byk-c-input-disabled-border);
  }
</style>


<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { fade } from 'svelte/transition'

  export let options = []
  export let group = []
	
  export let label = false
  export let validate
  export let validity
  export let validationHints = true
</script>