<div class="byk-field" class:byk-has-label={!!label}>
  {#if label}
  <label class="byk-field__label" for={id}>{label}</label>
  {/if}
  <div
    class="byk-field__control"
    class:m={size === 'm'}
    class:l={size === 'l'}
  >
    {#if showHex}
    <input
      type="text"
      bind:value={value}
      aria-label={ariaLabel}
      {disabled}
      {readonly}
      on:change
      on:blur
      on:keyup
    />
    {/if}
    <input
      id={id}
      type="color"
      bind:value={value}
      aria-label={ariaLabel}
      {disabled}
      {readonly}
      on:change
      on:blur
      on:keyup
    />
  </div>
</div>

<script>
  export let id = `colour_picker_${Math.random().toString(36).substr(2, 9)}`
  export let label = false
  export let value = null
  export let ariaLabel = label
  export let disabled = false
  export let readonly = false
  export let showHex = true
  export let size = 's'
</script>

<style>
  .byk-field__label {
	  display: block;
		font-weight: 600;
		margin-bottom: 0.6rem;
    color: var(--byk-c-label);
	}
	
	.byk-field__control {
		border: 1px solid var(--byk-c-control-border);
		display: inline-flex;
		align-items: stretch;
	}

	input[type="text"] {
	  border: none;
	  color: inherit;
    font-family: inherit;
    font-size: .9rem;
	  padding: 3px;
	  text-transform: uppercase;
	}

	input[type="color"] {
	  border: none;
	  cursor: pointer;
	}

	.m input[type="color"] {
	  height: 40px;
    width: 40px;
	}

	.l input[type="color"] {
	  height: 60px;
    width: 60px;
	}

	input[disabled],
  input[readonly] {
    outline: none;
  }

  input[readonly] {
    color: var(--byk-c-readonly-text);
    font-style: italic;
  }

  input[disabled] {
    background-color: var(--byk-c-disabled-background);
    border-color: var(--byk-c-disabled-border);
    color: var(--byk-c-disabled-text);
    font-style: normal;
  }

  input[type="color"][disabled] {
    cursor: not-allowed;
    filter: opacity(30%);
  }
</style>
