<div class="byk-field field location-lookup" class:byk-has-label={!!label} use:validate={value} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  {#if label}
  <span class="byk-field__label label">{label}</span>
  {/if}
  <div class="byk-field__control">
    <Geocoder
      {accessToken}
      {placeholder}
      on:result={setResult}
      on:clear={clear}
      {options}
      {value} />
    <span
      class="byk-field__icon"
      class:byk-is-danger={!$validity.valid}
      class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
    </span>
  </div>
  {#if validationHints && $validity.dirty && !$validity.valid}
  <p class="byk-field__help" transition:fade>{$validity.message}</p>
  {/if}
</div>

<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { Geocoder } from '@beyonk/svelte-mapbox'
  import { createEventDispatcher } from 'svelte'
  import { fade } from 'svelte/transition'

  const dispatch = createEventDispatcher()

  export let accessToken
  export let label = false
  export let value = null
  export let options = {}
  export let validate
  export let validity
  export let placeholder = null
  export let validationHints = true

  let selected = null

  function setResult (e) {
    selected = e.detail.result
    dispatch('result', e.detail)
  }

  function clear () {
    dispatch('clear')
  }
</script>

<style lang="scss">
  .byk-field {
		display: flex;
		flex-direction: column;
    margin: 6px 0;
    flex-shrink: 0;
	}

  .byk-field.byk-has-label {
    margin: 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}
	
	.byk-field .byk-field__control {
		border: 1px solid var(--byk-c-control-border);
		display: flex;
		align-items: center;
	}

  .byk-field.byk-field--invalid .byk-field__control {
		border: 1px solid var(--byk-c-danger);
  }
	
	.byk-field .byk-field__control .byk-field__icon {
		height: 1.25rem;
	}
	
	:global(.byk-field .byk-field__control .byk-field__icon svg) {
		width: 1.25rem;
		height: 1.25rem;
    stroke: var(--byk-c-primary);
	}

  :global(.byk-field .byk-field__control .byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field .byk-field__control .byk-is-success svg) {
		stroke: var(--byk-c-success);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}

  :global(.location-lookup .mapboxgl-ctrl-geocoder.mapboxgl-ctrl-geocoder) {
    box-shadow: none;
    display: flex;
    flex: 1;
    width: 100%;
    max-width: unset;
  }

  :global(.location-lookup .mapboxgl-ctrl-geocoder--icon.mapboxgl-ctrl-geocoder--icon) {
    top: 4px;
    margin-top: 0px;
  }

  :global(.byk-field .byk-field__control > div) {
    flex: 1;
  }

  :global(.location-lookup .mapboxgl-ctrl-geocoder--input.mapboxgl-ctrl-geocoder--input) {
    font-family: 'Cabin', sans-serif;
    font-size: 1rem;
    padding: 6px 12px;
    margin-left: 36px;
    height: 2rem;
  }
</style>