<div class="byk-field" class:byk-has-label={!!label} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  {#if label}
  <span class="byk-field__label">{label}</span>
  {/if}
  <div class="byk-field__control">
    <div class="options">
    {#each options as option (option.value)}
    <label class="byk-field__control__radio">
      <span class="byk-field__control__radio--label">{option.key}</span>
      <input type="radio" value={option.value} bind:group={value} use:validate={value} on:change on:blur />
      <div class="indicator"></div>
      {#if option.description}
      <div>
        {@html option.description}
      </div>
      {/if}
    </label>
    {/each}
    </div>
    <span
      class="byk-field__icon"
      class:byk-is-danger={!$validity.valid}
      class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
    </span>
    {#if validationHints && $validity.dirty && !$validity.valid}
    <p class="byk-field__help" transition:fade>{$validity.message}</p>
    {/if}
  </div>
</div>

<style lang="scss">
  .byk-field {
		display: flex;
    flex-shrink: 0;
		flex-direction: column;
    margin: 6px 0;
	}

  .byk-field.byk-has-label {
    margin: 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}
	
	.byk-field .byk-field__control {
		display: flex;
		align-items: flex-end;
	}
	
	.byk-field .byk-field__control .byk-field__icon {
		height: 1.25rem;
    margin: 0 6px;
	}
	
	:global(.byk-field .byk-field__control .byk-field__icon svg) {
		width: 1.25rem;
		height: 1.25rem;
    stroke: var(--byk-c-primary);
	}

  :global(.byk-field .byk-field__control .byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field .byk-field__control .byk-is-success svg) {
		stroke: var(--byk-c-success);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}

  .byk-field__control__radio {
    display: block;
    position: relative;
    padding-left: 30px;
    margin-bottom: 8px;
    cursor: pointer;
    font-size: 18px;
    font-size: 14px;

    input {
      position: absolute;
      z-index: -1;
      opacity: 0;

      &:checked ~ .indicator {
        background: var(--byk-c-primary);
      }

      &:checked ~ .indicator:after {
        display: block;
      }
    }

    &:hover input ~ .indicator,
    input:focus ~ .indicator {
      background: var(--byk-c-input-background);
    }

    &:hover input:not([disabled]):checked ~ .indicator,
    input:checked:focus ~ .indicator {
      background: var(--byk-c-primary);
    }

    .indicator {
      position: absolute;
      top: 2px;
      left: 0;
      height: 20px;
      width: 20px;
      border: 1px solid var(--byk-c-control-border);
      border-radius: 50%;

      &:after {
        content: '';
        position: absolute;
        display: none;
        left: 6px;
        top: 6px;
        height: 6px;
        width: 6px;
        border-radius: 50%;
        background: var(--byk-c-input-background);
      }
    }
  }

  .byk-field.byk-field--invalid .byk-field__control .options .indicator {
		border: 1px solid var(--byk-c-danger);
  }

  .byk-field__control__radio--label {
    font-weight: 600;
  }
</style>

<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { fade } from 'svelte/transition'

  export let options = []
  export let value
  export let validate
  export let validity
  export let label = false
  export let validationHints = true
</script>