 <div class="byk-field" class:byk-has-label={!!label} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  {#if label}
  <label for={fieldId} class="byk-field__label">{label}</label>
  {/if}
  <div class="byk-field__control byk-field__control--select">
    <select id={fieldId} class="byk-select" bind:value={value} use:validate={value} on:change on:blur {disabled}>
      {#if options}
        {#if placeholder}
        <option value={null} disabled selected>
          {placeholder}
        </option>
        {/if}
        {#each options as option (option.value)}
        <option value={option.value}>
          {option.name}
        </option>
        {/each}
      {:else}
      <slot></slot>
      {/if}
    </select>
    <span 
      class="byk-field__icon"
      class:byk-is-danger={!$validity.valid}
      class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
    </span>
  </div>
  {#if validationHints && $validity.dirty && !$validity.valid}
  <p class="byk-field__help" transition:fade>{$validity.message}</p>
  {/if}
</div>

<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { fade } from 'svelte/transition'

  const fieldId = `byk_id_${Math.random().toString(36).substring(0, 8)}`

  export let label
  export let value = null
  export let placeholder = null
  export let options = null
  export let validate
  export let validity
  export let disabled = false

  export let validationHints = true
</script>

<style>
  .byk-field {
		display: flex;
		flex-direction: column;
    flex-shrink: 0;
    margin: 6px 0;
	}

  .byk-field.byk-has-label {
    margin: 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}
	
	.byk-field .byk-field__control {
    border: 1px solid var(--byk-c-control-border);
		display: flex;
    align-items: center;
    background-color: var(--byk-c-white);
	}

  .byk-field.byk-field--invalid .byk-field__control {
		border: 1px solid var(--byk-c-danger);
  }
	
	.byk-field .byk-field__control .byk-select {
    font-family: inherit;
    padding: 6px 12px;
    font-size: 1rem;
		flex: 1;
		border: 0;
		margin: 0;
		outline: 0;
    width: 100%;
	}
	
	.byk-field .byk-field__control .byk-field__icon {
		height: 1.25rem;
    margin: 0 6px;
	}
	
	:global(.byk-field .byk-field__control .byk-field__icon svg) {
		width: 1.25rem;
		height: 1.25rem;
    stroke: var(--byk-c-primary);
	}

  :global(.byk-field .byk-field__control .byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field .byk-field__control .byk-is-success svg) {
		stroke: var(--byk-c-success);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}

  .byk-select {
    display: flex;
    flex-shrink: 0;
    font-size: 16px;
    color: #444;
    line-height: 1.3;
    padding: .6em 1.4em .5em .8em;
    flex: 1;
    box-sizing: border-box;
    margin: 0;
    border: none;
    appearance: none;
    -webkit-appearance: none;
    -moz-appearance: none;
    outline: none;
    background: var(--byk-c-white) url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100%' height='100%' fill='none' viewBox='0 0 24 24' stroke='hsla(175, 93%, 33%, 1)' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='feather feather-chevron-down'><polyline points='6 9 12 15 18 9'></polyline></svg>") no-repeat;
    background-position: right 5px top 50%;
  }

  .byk-field.byk-field--invalid .byk-field__control .byk-select {
    background: var(--byk-c-white) url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100%' height='100%' fill='none' viewBox='0 0 24 24' stroke='red' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='feather feather-chevron-down'><polyline points='6 9 12 15 18 9'></polyline></svg>") no-repeat;
    background-position: right 5px top 50%;
  }

  .byk-select:active {
    background: var(--byk-c-white) url("data:image/svg+xml;utf8,<svg xmlns='http://www.w3.org/2000/svg' width='100%' height='100%' fill='none' viewBox='0 0 24 24' stroke='hsla(175, 93%, 33%, 1)' stroke-width='2' stroke-linecap='round' stroke-linejoin='round' class='feather feather-chevron-up'><polyline points='18 15 12 9 6 15'></polyline></svg>") no-repeat;
    background-position: right 5px top 50%;
  }

  .byk-select option {
    font-weight: normal;
  }

  .byk-select:disabled {
    color: var(--byk-c-disabled-text);
  }
</style>
