<div class="byk-field byk-field__textarea" class:byk-has-label={!!label} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  {#if label}
  <label for={fieldId} class="byk-field__label">{label}</label>
  {/if}
  <div class="byk-field__control">
    <textarea
      id={fieldId}
      {placeholder}
      bind:this={el}
      bind:value={value}
      aria-label={ariaLabel}
      on:change
      on:blur
      on:keyup
      {maxlength}
      {minlength}
      use:validate={value} />
    <span
      class="byk-field__icon"
      class:byk-is-danger={!$validity.valid}
      class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
    </span>
  </div>
  {#if validationHints && $validity.dirty && !$validity.valid}
  <p class="byk-field__help" transition:fade>{$validity.message}</p>
  {/if}
</div>

<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { fade } from 'svelte/transition'

  export let value = null
  export let label = false
  export let validate
  export let validity
  export let placeholder
  export let ariaLabel = label
  export let validationHints = true
  export let autoResize = false
  export let maxlength
  export let minlength

  const fieldId = `byk_id_${Math.random().toString(36).substring(0, 8)}`

  let el

  function resize () {
    if (!value) {
      el.style.height = '8rem'
      return
    }
    el.style.height = el.scrollHeight + 'px'
  }

  $: autoResize && el && value && resize()
</script>

<style>
  .byk-field {
		display: flex;
    flex-shrink: 0;
		flex-direction: column;
    margin: 6px 0;
	}

  .byk-field.byk-has-label {
    margin: 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}
	
	.byk-field .byk-field__control {
		border: 1px solid var(--byk-c-control-border);
		display: flex;
		align-items: flex-end;
	}

  .byk-field.byk-field--invalid .byk-field__control {
		border: 1px solid var(--byk-c-danger);
  }
	
	.byk-field .byk-field__control textarea {
    font-family: inherit;
    padding: 12px;
    font-size: 1rem;
		flex: 1;
		border: 0;
		margin: 0;
		outline: 0;
    height: 8rem;
	}
	
	.byk-field .byk-field__control .byk-field__icon {
		height: 1.5rem;
    margin: 6px;
	}
	
	:global(.byk-field.byk-field__textarea .byk-field__control .byk-field__icon svg) {
		width: 1.5rem;
		height: 1.5rem;
    stroke: var(--byk-c-primary);
	}

  :global(.byk-field.byk-field__textarea .byk-field__control .byk-field__icon.byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field.byk-field__textarea .byk-field__control .byk-field__icon.byk-is-success svg) {
		stroke: var(--byk-c-success);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}
</style>