<div class="byk-field" class:byk-has-label={!!label} class:byk-field--invalid={$validity.dirty && !$validity.valid}>
  {#if label}
  <label for={fieldId} class="byk-field__label">{label}</label>
  {/if}
  <div class="byk-field__control">
    {#if icon}
    <span class="byk-field__icon">
      <svelte:component this={icon} />
    </span>
    {/if}
    {#if type === 'text'}
      <input
        id={fieldId}
        type="text"
        bind:value={value}
        {placeholder}
        aria-label={ariaLabel}
        use:validate={value}
        on:change
        on:blur
        on:keyup
        {maxlength}
        {minlength}
        >
    {:else if type === 'password'}
      <input
        id={fieldId}
        type="password"
        bind:value={value}
        {placeholder}
        aria-label={ariaLabel}
        use:validate={value}
        on:change
        on:blur
        on:keyup
        {maxlength}
        {minlength}
        >
    {:else if type === 'time'}
      <input
        id={fieldId}
        type="time"
        {step}
        bind:value={value}
        {placeholder}
        aria-label={ariaLabel}
        use:validate={value}
        on:change
        on:blur
        on:keyup
        >
    {:else if type === 'number'}
      <input
        id={fieldId}
        type="number"
        bind:value={value}
        {placeholder}
        aria-label={ariaLabel}
        use:validate={value}
        on:change
        on:blur
        on:keyup
        >
    {/if}
    <span 
      class="byk-field__icon"
      class:byk-is-danger={!$validity.valid}
      class:byk-is-success={$validity.valid}>
        {#if $validity.dirty}
          {#if $validity.valid}
            <CheckIcon />
          {:else}
            <XIcon />
          {/if}
        {/if}
    </span>
  </div>
  {#if validationHints && $validity.dirty && !$validity.valid}
  <p class="byk-field__help" transition:fade>{$validity.message}</p>
  {/if}
</div>

<script>
  import { CheckIcon, XIcon } from 'svelte-feather-icons'
  import { fade } from 'svelte/transition'

  const fieldId = `byk_id_${Math.random().toString(36).substring(0, 8)}`

  export let icon = false
  export let label = false
  export let type = 'text'
  export let step = null
  export let value = null
  export let validate
  export let validity
  export let placeholder
  export let ariaLabel = label
  export let validationHints = true
  export let maxlength
  export let minlength

  $: if (value === '') {
    value = null
  }
</script>

<style>
  .byk-field {
		display: flex;
		flex-direction: column;
    flex-shrink: 0;
	  margin: 6px 0;
	}

  .byk-field.byk-has-label {
    margin: 0;
  }
	
	.byk-field .byk-field__label {
		font-weight: 600;
		margin: 0.6rem 0;
    color: var(--byk-c-label);
	}
	
	.byk-field .byk-field__control {
		border: 1px solid var(--byk-c-control-border);
		display: flex;
		align-items: center;
	}

  .byk-field.byk-field--invalid .byk-field__control {
		border: 1px solid var(--byk-c-danger);
  }
	
	.byk-field .byk-field__control input {
    font-family: inherit;
    padding: 6px 12px;
    font-size: 1rem;
		flex: 1;
		border: 0;
		margin: 0;
		outline: 0;
    width: 100%;
	}
	
	.byk-field .byk-field__control .byk-field__icon {
		height: 1.25rem;
    margin: 0 6px;
	}
	
	:global(.byk-field .byk-field__control .byk-field__icon svg) {
		width: 1.25rem;
		height: 1.25rem;
    stroke: var(--byk-c-primary)
	}

  :global(.byk-field .byk-field__control .byk-is-danger svg) {
		stroke: var(--byk-c-danger);
  }

  :global(.byk-field .byk-field__control .byk-is-success svg) {
		stroke: var(--byk-c-success);
  }
	
	.byk-field .byk-field__help {
		display: flex;
		justify-content: flex-end;
		margin: 0.4rem;
		color: var(--byk-c-danger);
	}
</style>
